import React from 'react';

class ClientSelected extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      id : '',
      email : '',
      fitstname : '',
      lastname : '',
      flash : {},
    }

  }

  componentDidMount(){
    let url = window.location.href
    var arrayURL = url.split("/");
    console.log("url ", arrayURL)
    this.setState({
      email : arrayURL[7],
      lastname : arrayURL[6],
      firstname : arrayURL[5],
      id : arrayURL[4]
    })
  }


  handleChange = (event) => {
    const {target} = event;
    this.setState({
      [target.name] : target.value
    })
  }


  handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.target);

    const email = data.get("email");
    const firstname = data.get("firstname");
    const lastname = data.get("lastname");

    const dataForm = {
      firstname : firstname,
      lastname : lastname,
      email : email
    }

    const options = {
      method : 'PUT',
      headers : {
        'Accept' : 'application/json',
        'Content-Type' : 'application/json'
      },
      options : {
        mode : 'no-cors'
      },
      body : JSON.stringify(dataForm)
    };

    const urlApi = `http://localhost:5000/api/clients/${this.state.id}`;

    fetch(urlApi, options)
    .then(response  =>  response.json())
    .then(
        res  =>  this.setState({"flash":  res.flash}),
        err  =>  this.setState({"flash":  err.flash})
    )
  }

  deleteClient = () => {
    const options = {
      method : 'DELETE'
    }
    const urlApi = `http://localhost:5000/api/clients/${this.state.id}`;

    fetch(urlApi, options)
    .then(response  =>  response.json())
  }

  render(){
    return (
      <div className="auth-wrapper">
        <div className="auth-inner">
          <div>
            <form onSubmit={this.handleSubmit}>
                <h3>Modify</h3>

                <div className="form-group">
                    <label htmlFor="firstname">Firsname</label>
                    <input id="firstname" name="firstname" type="text" placeholder={this.state.firstname} className="form-control" onChange={this.handleChange} />
                </div>

                <div className="form-group">
                    <label htmlFor="lastname">Lastname</label>
                    <input id="lastname" name="lastname" type="text" className="form-control" placeholder={this.state.lastname}  onChange={this.handleChange} />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input id="email" name="email" type="email" className="form-control" placeholder={this.state.email}  onChange={this.handleChange} />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Save</button>
            </form>

              <p> </p>
              <button type="submit" className="btn btn-primary btn-block">Delete client</button>
          </div>
      </div>
    </div>
    )
  }
}


export default ClientSelected;
