import React from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route, NavLink} from "react-router-dom";

import Client from "./Client.js";
import Contact from "./Contact";
import ClientSelected from "./ClientSelected"

class Page extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      clientSelected : [],
      open :  false
    }
  }

  selectedClient = (client) => {
    this.setState({
      clientSelected : client,
      open : true
    })
  }

  // updateAllClients = (clients) =>{
  //   this.props.updateClients(clients);
  // }

  render(){
    return (<Router>
      <div className="App">

        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container">
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
              <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/clients/new"}>Add client</NavLink>
                </li>
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/clients"}>Client's list</NavLink>
                </li>
              </ul>
            </div>
          </div>
        </nav>

            <Switch>
              <Route path="/clients/new" render={ () => (
                <Client

                />)}
              />
              <Route to="/clients" render={ () => (
                <Contact
                  selectedClient={this.selectedClient}
                />)}
              />

              <Route to={`/clients/${this.state.clientSelected["id"]}`} render={ () => (
                <ClientSelected
                  selectedClient={this.state.clientSelected}
                />)}
              />
            </Switch>

      </div></Router>
    );
  }

}

export default Page;
